// Test virtual modules generated by SMRT Vite plugin
try {
  console.log('Testing virtual module imports...');

  // Test if we can import virtual modules
  const routes = await import('@smrt/routes');
  console.log('‚úÖ @smrt/routes imported successfully:', typeof routes.default);

  const client = await import('@smrt/client');
  console.log('‚úÖ @smrt/client imported successfully:', typeof client.default);

  const mcp = await import('@smrt/mcp');
  console.log('‚úÖ @smrt/mcp imported successfully:', typeof mcp.default);

  const manifest = await import('@smrt/manifest');
  console.log(
    '‚úÖ @smrt/manifest imported successfully:',
    typeof manifest.default,
  );

  console.log('\nüéâ All virtual modules are accessible!');
} catch (error) {
  console.error('‚ùå Error importing virtual modules:', error.message);
  process.exit(1);
}
